mapper.select:
select p.code from sys_permission p where p.id in(
    select gp.permission_id from sys_group_permission gp where gp.group_id in(
        select rg.group_id from sys_role_group rg where rg.role_id in (
            select ur.role_id from sys_user_role ur where ur.user_id=#{userId}
        )
    )
)
